import { Component } from '@angular/core';
import flagData from './data.json';

@Component({
  selector: 'app-flags',
  templateUrl: './flags.component.html',
  styleUrls: ['./flags.component.css']
})
export class FlagsComponent {

  flags = flagData;



  log(){
    console.log(this.flags[249]);
  }

  searchText: string ='';
  regionText: string='';
  onSearchTextEntered(searchValue: string){
    this.searchText = searchValue;
    console.log(this.searchText);
  }

  onRegionEntered(regionValue: string){
    this.regionText = regionValue;
    console.log(this.regionText);
  }
}
<app-filter 
(searchTextChanged)="onSearchTextEntered($event)"
(regionChanged)="onRegionEntered($event)"
></app-filter>

<div class="wrapper">
  <div *ngFor="let item of flags" >
    <div *ngIf="regionText==='All' || item.region.includes(regionText)">
      <div *ngIf="searchText === '' || item.name.toLowerCase().includes(searchText)" >
         <div class="card">
          <img [src]=item.flags.svg alt="">
          <p class="country-name">{{item.name}}</p>
          <p>Region: {{item.region}}</p>
          <p>Populaiton: {{item.population}}</p>
          <p>Capital: {{item.capital}}</p>

        </div>
      </div>

    </div>


  </div>
</div>
